import React,{useState} from 'react'
import"./Compo.css";

const Room = () => {
    const [images, setImages] = useState([]);

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const files = event.target.files;

    if (files) {
      const imageArray = Array.from(files).map((file) => URL.createObjectURL(file));
      setImages([...images, ...imageArray]);
    }
  };

  // Function to remove an uploaded image
  const handleRemoveImage = (index) => {
    const newImages = [...images];
    newImages.splice(index, 1);
    setImages(newImages);
  };
  return (
    <div >
    <section className='sectionmain'>
    <h1 className='head'>Multiple Image Upload and Display</h1>

    {/* Input field for image upload */}
    /*<input type="file" accept="image/*" multiple onChange={handleImageUpload} />*/}

    {/* Display the uploaded images */}
    {images.length > 0 && (
      <div>
        <h2>Uploaded Images:</h2>
        <div className="image-list">
          {images.map((imageUrl, index) => (
            <div key={index} className="image-container">
              <img src={imageUrl} alt={`Uploaded ${index}`} width="200" />
              <button onClick={() => handleRemoveImage(index)}>Remove</button>
            </div>
          ))}
        </div>
      </div>
    )}
    </section>
  </div>

  )
}

export default Room
