import React,{useState} from 'react'
import "./Compo.css";

const Room = () => {
    const [image, setImage] = useState(null);

    // Function to handle image upload
    const handleImageUpload = (event) => {
      const file = event.target.files[0];

      if (file) {
        const reader = new FileReader();

        reader.onload = (e) => {
          // Set the uploaded image as the state
          setImage(e.target.result);
        };

        reader.readAsDataURL(file);
      }
    };
  return (
    <div >
    <section className='sectionmain'>
    <div>
      <h1 className='head'>Image Upload and Display</h1>


     <label class="file-input-container">
     <input type="file" accept="image/*" onChange={handleImageUpload}/>
     <span class="file-input-label">Upload Photo</span>
 </label>

      {image && (
        <div className='img'>
          <h2 className='head2'>Uploaded Image:</h2>
          <img src={image} alt="Uploaded" width="900" height="900" />
        </div>
      )}
    </div>
    </section>
  </div>

  )
}

export default Room
