import React, { useRef, useState } from 'react';


function Canvas() {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [drawing, setDrawing] = useState(false);
  const [points, setPoints] = useState([]);
  const [fillColor, setFillColor] = useState('red');
  const [shapes, setShapes] = useState([]);

  const handleMouseDown = (e) => {
    setDrawing(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.strokeStyle = fillColor;
    ctx.lineWidth = 2;
    ctx.beginPath();
    const { offsetX, offsetY } = e.nativeEvent;
    ctx.moveTo(offsetX, offsetY);
    setPoints([...points, { x: offsetX, y: offsetY }]);
  };

  const handleMouseMove = (e) => {
    if (!drawing) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const { offsetX, offsetY } = e.nativeEvent;
    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
    setPoints([...points, { x: offsetX, y: offsetY }]);
  };

  const handleMouseUp = () => {
    setDrawing(false);
    const newShape = [...points];
    setShapes([...shapes, newShape]);
    setPoints([]);
  };

  const handleFillColorChange = (e) => {
    setFillColor(e.target.value);
  };

  const fillPolygon = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = fillColor;

    shapes.forEach((shape) => {
      ctx.beginPath();
      shape.forEach((point, index) => {
        if (index === 0) {
          ctx.moveTo(point.x, point.y);
        } else {
          ctx.lineTo(point.x, point.y);
        }
      });
      ctx.closePath();
      ctx.fill();
    });
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setPoints([]);
    setShapes([]);
  };

  return (
    <div className="App">
      <h1>Draw and Fill Polygons</h1>


      <section className='sectionmain'>
      <h1>Image Upload and Polygon Drawing</h1>

      {/* Input field for image upload */}
      <div>
      <label class="file-input-container">
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      <span class="file-input-label">Choose a File</span>
      </label>
      </div>
      <div class="image-container">
      {/* Display the uploaded image */}
      {image && (
        <div>
          <h2>Uploaded Image:</h2>
          <img src={image} alt="Uploaded"  width={400}
          height={400} />
        </div>
      )}
      </div>
      </section>
      <div>
        <input type="color" value={fillColor} onChange={handleFillColorChange} />
        <button onClick={fillPolygon}>Fill</button>
        <button onClick={clearCanvas}>Clear</button>
      </div>
      <canvas
        ref={canvasRef}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      />
    </div>
  );
}

export default Canvas;
