import React, { useState, useRef, useEffect } from 'react';

function Fabric() {
  const [image, setImage] = useState(null);
  const [drawing, setDrawing] = useState(false);
  const [polygonPoints, setPolygonPoints] = useState([]);
  const canvasRef = useRef(null);

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Function to handle polygon drawing
  const handlePolygonDrawing = (e) => {
    if (!drawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setPolygonPoints([...polygonPoints, { x, y }]);

    // Draw the polygon on the canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    drawImage(image);
    drawPolygon(ctx, polygonPoints);
  };

  // Function to start drawing a polygon
  const startDrawing = () => {
    setDrawing(true);
    setPolygonPoints([]);
  };

  // Function to stop drawing a polygon
  const stopDrawing = () => {
    setDrawing(false);
  };

  // Function to draw an image on the canvas
  const drawImage = (imageUrl) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.src = imageUrl;
    ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
  };

  // Function to draw a polygon on the canvas
  const drawPolygon = (ctx, points) => {
    if (points.length < 2) return;

    ctx.strokeStyle = 'blue';
    ctx.lineWidth = 2;
    ctx.lineJoin = 'round';
    ctx.beginPath();
    ctx.moveTo(points[0].x, points[0].y);

    for (let i = 1; i < points.length; i++) {
      ctx.lineTo(points[i].x, points[i].y);
    }

    ctx.closePath();
    ctx.stroke();
  };

  // Clear canvas and reset drawing when the image changes
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setDrawing(false);
    setPolygonPoints([]);
  }, [image]);

  return (
    <div>
      <h1>Image Upload and Polygon Drawing</h1>

      {/* Input field for image upload */}
      <input type="file" accept="image/*" onChange={handleImageUpload} />

      {/* Display the uploaded image */}
      {image && (
        <div>
          <h2>Uploaded Image:</h2>
          <img src={image} alt="Uploaded" width="400" />
        </div>
      )}

      {/* Canvas for drawing polygons */}
      {image && (
        <div>
          <h2>Draw a Polygon:</h2>
          <canvas
            ref={canvasRef}
            width={400}
            height={400}
            onMouseDown={startDrawing}
            onMouseMove={handlePolygonDrawing}
            onMouseUp={stopDrawing}
          />
        </div>
      )}
    </div>
  );
}

export default Fabric;
