import React, { useRef, useEffect } from 'react';
import { fabric } from 'fabric';

const Fabric = ({ imageUrl }) => {
    const canvasRef = useRef(null);


    useEffect(() => {
      const canvas = new fabric.Canvas(canvasRef.current, {
        width: 800, // Set your canvas width
        height: 400, // Set your canvas height
      });

      if (imageUrl) {
        fabric.Image.fromURL(imageUrl, (img) => {
          canvas.setBackgroundImage(img, canvas.renderAll.bind(canvas));
        });
      }
    }, [imageUrl]);
    // Inside your DrawingCanvas component
const drawingTool = new fabric.Polygon([], {
    fill: 'transparent',
    stroke: 'blue', // Set the polygon's outline color
    strokeWidth: 2, // Set the outline width
    selectable: false, // Prevent selection of polygons
  });

  let isDrawing = false;
  let points = [];

  canvas.on('mouse:down', (e) => {
    isDrawing = true;
    const pointer = canvas.getPointer(e.e);
    points = [pointer.x, pointer.y];
    drawingTool.set({ points });
    canvas.add(drawingTool);
  });

  canvas.on('mouse:move', (e) => {
    if (!isDrawing) return;
    const pointer = canvas.getPointer(e.e);
    points = [...points, pointer.x, pointer.y];
    drawingTool.set({ points });
    canvas.renderAll();
  });

  canvas.on('mouse:up', () => {
    isDrawing = false;
    drawingTool.setCoords();
    canvas.discardActiveObject();
  });
  return (
    <>
    <canvas ref={canvasRef} />
    </>
  )
}

export default Fabric
