import React, { useRef, useEffect } from 'react';

function ColorPicker({ selectedColor, onSelectColor }) {
  const colorInputRef = useRef(null);

  useEffect(() => {
    const jscolorInstance = jscolor.install(colorInputRef.current);
    jscolorInstance.fromString(selectedColor);
    jscolorInstance.onFineChange = function () {
      onSelectColor(this.toHEXString());
    };
  }, [selectedColor, onSelectColor]);

  return (
    <div>
      <h2>Color Picker</h2>
      <input
        type="text"
        ref={colorInputRef}
        className="jscolor {onFineChange:'updateColorPicker'}"
      />
    </div>
  );
}

export default ColorPicker;
